namespace Strings_Making_Anagrams
{
    class Program
    {
        static void Main(string[] args)
        {
            var x = "";
            var y = "";
            //string a = Console.ReadLine();
            //string b = Console.ReadLine();
            //var inter = x.Intersect(y).OrderBy(c=>c).ToArray();
            //var inter = a.Intersect(b).OrderBy(c => c).ToArray();
            //Console.WriteLine(x.Length-inter.Length+y.Length-inter.Length); //732
            //Console.WriteLine(a.Length-inter.Length+b.Length-inter.Length); //732
            //foreach (var VARIABLE in inter)
            //{
            //    Console.Write($"{VARIABLE} ");
            //}
            var list = new List<string>() { x, y }.Select(z=>new string(z.OrderBy(c => c).ToArray()));
            string shortest = list.OrderBy(s => s.Length).First();//a.Length <= b.Length ? a : b; 
            IEnumerable<string> shortestSubstrings = shortest.GetAllSubstrings().OrderByDescending(s => s.Length);
            var other = list.Where(s => s != shortest).ToArray();
            string longestCommonIntersection = string.Empty;
            foreach (string subStr in shortestSubstrings)
            {
                bool allContains = other.All(s => s.Contains(subStr));
                if (allContains)
                {
                    longestCommonIntersection = subStr;
                    break;
                }
            }
            Console.Write("longestCommonIntersection=" + longestCommonIntersection.Length);

            Console.ReadKey();
        }

        static bool IsAnagram(string one, string two)
        {
            return one.OrderBy(c => c).SequenceEqual(two.OrderBy(c => c));
        }
    }

    public static class MyClass
    {
        public static IEnumerable<string> GetAllSubstrings(this string word)
        {
            return from charIndex1 in Enumerable.Range(0, word.Length)
                   from charIndex2 in Enumerable.Range(0, word.Length - charIndex1 + 1)
                   where charIndex2 > 0
                   select word.Substring(charIndex1, charIndex2);
        }
    }
}
