using System;
using System.Collections.Generic;
using System.Linq;

namespace Strings_Making_Anagrams
{
    class Program
    {
        static void Main(string[] args)
        {
            var x = "";
            var y = "";//732
            string a = x;//Console.ReadLine();
            string b = y;//Console.ReadLine();
            const string alphabet = "abcdefghijklmnopqrstuvwxyz";
            var aAlphaCount = new int[alphabet.Length];
            var bAlphaCount = new int[alphabet.Length];
            for (int i = 0; i < a.Length; i++)
            {
                if (alphabet.Any(c => c == a[i])) aAlphaCount[Array.IndexOf(alphabet.ToCharArray(), a[i])]++;
            }
            for (int i = 0; i < b.Length; i++)
            {
                if (alphabet.Any(c => c == b[i])) bAlphaCount[Array.IndexOf(alphabet.ToCharArray(), b[i])]++;
            }
            var commonPartLenght = 0;
            for (int i = 0; i < bAlphaCount.Length; i++)
            {
                if (aAlphaCount[i] != 0 && bAlphaCount[i] != 0) commonPartLenght += Math.Min(aAlphaCount[i], bAlphaCount[i]);
            }
            Console.WriteLine(a.Length-commonPartLenght+b.Length-commonPartLenght);

            Console.ReadKey();
        }

        static bool IsAnagram(string one, string two)
        {
            return one.OrderBy(c => c).SequenceEqual(two.OrderBy(c => c));
        }
    }

    public static class MyClass
    {
        public static IEnumerable<string> GetAllSubstrings(this string word)
        {
            return from charIndex1 in Enumerable.Range(0, word.Length)
                   from charIndex2 in Enumerable.Range(0, word.Length - charIndex1 + 1)
                   where charIndex2 > 0
                   select word.Substring(charIndex1, charIndex2);
        }
    }
}
